import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import {
    Navigation,
    CallApi,
    Homepage,
    Routines,
    Login
} from './Components'

const {REACT_APP_BASE_URL = 'http://localhost:3000/api'} = process.env;

const App = () => {
    const [activities, setActivities] = ([]);
    const [loggedIn, setLoggedIn] = useState(false);
    const [routines, setRoutines] = useState([]);
    const [token, setToken] = useState('');

    console.log( "ReactUrl", `${REACT_APP_BASE_URL}`)


    const fetchActivities = async () => {
        try {
            const resp = await fetch (`${REACT_APP_BASE_URL}/activities`,{
                headers: {
                    'Content-Type': 'application/json'
            }});
            const data = await resp.json();
            if (data){
                setActivities(data);
            }

        } catch (error) {
            throw error;
        }
    }

    const fetchRoutines = async () => {
        try {
            const resp = await fetch (`${REACT_APP_BASE_URL}/routins`, {
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await resp.json();
            if (data){
                setRoutines(data)
            }

        } catch (error) {
            throw error
        }
    }
   useEffect(() => {
       try {
           fetchActivities();
           fetchRoutines();

       } catch (error) {
           console.error(error);

       }
   }, [token]);

    useEffect(() => {
        const foundToken = localStorage.getItem('token');
        if(foundToken) {
          setToken(foundToken)
          console.log("<<<< found:", foundToken)
        }
      }, []);
    return <>
        <div>
            <Navigation token={token} setToken= {setToken} loggedIn = {loggedIn} setLoggedIn = {setLoggedIn}/>
            <div>
                <Switch>
                    <Route exact path = "/home">
                        <Homepage />
                    </Route>

                    {/* <Route exact path = "/account">

                    </Route> */}

                    {/* <Route exact path = "/activities">
                        <Activities />
                    </Route> */}

                    <Route exact path = "/login">
                        <Login setToken= {setToken} token={token} />
                    </Route>

                    <Route exact path = "/routines">
                        <Routines routines={routines} setRoutines={setRoutines} token={token} fetchRoutines={fetchRoutines} />
                    </Route>

                </Switch>
            </div>
        </div>

    </>
}

ReactDOM.render(
    <Router>
        <App />
    </Router>,
  document.getElementById('app'),
);
