import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import {
    Navigation,
    CallApi,
    Homepage,
    Routines,
    Login
} from './Components'

const {REACT_APP_BASE_URL = 'http://localhost:3000/api'} = process.env;
const App = () => {
    const [username, setUsername] = useState();
    const [user, setUser] = useState();
    const [userID, setUserID] = useState();
    const [token, setToken] = useState([]);
    const [activities, setActivities] = useState();
    const [routines, setRoutines] = useState([]);

    console.log( "ReactUrl", `${REACT_APP_BASE_URL}`)


    const fetchRoutines = async () => {
        const resp = await fetch(`${REACT_APP_BASE_URL}/routines`);
        console.log("<<<<<resp: ", resp)
    }
    useEffect(() => {
        fetchRoutines();
    }, [token])

    useEffect(() => {
        const foundToken = localStorage.getItem('token');
        if(foundToken) {
          setToken(foundToken)
        }
      }, []);
    return <>
        <div>
            <Navigation username={username} token={token} setUsername = {setUsername} setToken= {setToken}/>
            <div>
                <Switch>
                    <Route exact path = "/home">
                        <Homepage />
                    </Route>

                    {/* <Route exact path = "/account">

                    </Route> */}

                    {/* <Route exact path = "/activities">
                        <Activities />
                    </Route> */}

                    <Route exact path = "/login">
                        <Login username={username} setUsername = {setUsername} setToken= {setToken} token={token} setUser={setUser} setUserID={setUserID} />
                    </Route>

                    <Route exact path = "/routines">
                        <Routines username = {username} routines={routines} setRoutines={setRoutines} token={token} fetchRoutines={fetchRoutines} />
                    </Route>

                </Switch>
            </div>
        </div>

    </>
}

ReactDOM.render(
    <Router>
        <App />
    </Router>,
  document.getElementById('app'),
);
