import React, { useState } from 'react';
import {SingleActivity} from './'

const Activities = ({activities, token, fetchActivities}) => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    const newActivityForm = () => {

        const handleSubmit = async (event) => {
            event.preventDefault();
            try {
                const resp = await fetch(`${REACT_APP_BASE_URL}/activities`, {
                   method: 'POST',
                   headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                   }, body: JSON.stringify ({name, description})
                })
                const data = await resp.json();
                if(data) {
                    setName('');
                    setDescription('');
                    await fetchActivities();
                }
                return;

            } catch (error) {
                console.error(error)
            }
        }
    }
    return activities ?
        <>
            <h3 className='activitiesheader'>Activities</h3>
                <div className='activities'>
                {
                    activities.map(activity => <SingleActivity key = {activity.id} activity = {activity} />)
                }
            </div>

        </>
        : 'Rendering...'
};

export default Activities;
