import React, {useState, useEffect} from 'react';

import { SingleRoutine } from './'
const { REACT_APP_BASE_URL = 'https://fitnesstrac-kr.herokuapp.com/api/' } = process.env;

const MyRoutines = ({username, token, setMyRoutines, myRoutines}) => {

    const fetchMyRoutines = async () => {
        console.log("<<<<", resp)
        try {
            if(username) {
                const resp = await fetch (`${REACT_APP_BASE_URL}/users/${user.name}/routines`, {
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`
                    },
                })
                const data = await resp.json();
                if (data) {
                    setMyRoutines(data);
                } else {
                    setMyRoutines([]);
                }
            }
            return

        } catch (error) {
            console.error(error)
        };
    };

    const handleDelete = async (routineId) => {
        try {
            await fetch(`${REACT_APP_BASE_URL}/routines/${routineId}`, {
               method: 'DELETE',
               headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
               }
            })
            await fetchMyRoutines();

        } catch (error) {
            console.error(error)
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await fetch(`${REACT_APP_BASE_URL}/routines`, {
               method: 'POST',
               headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
               }, body: JSON.stringify ({name, goal, isPublic})
            })
            const data = await response.json();
            if(data) {
                setName('');
                setGoal('');
                setIsPublic(false);
                await fetchMyRoutines();
            }
            return;

        } catch (error) {
            console.error(error)
        }
    }

    useEffect(() => {
        try {
            fetchMyRoutines();
        } catch (error) {
            console.error(error)
        }
    }, [])

    return <>
        {
            myRoutines.length > 0 ?
                myRoutines.map(routine => {
                    return <SingleRoutine key = {routine.id} routine = {routine}>

                    </SingleRoutine>
                }) : null
        }
        </>




}
export default MyRoutines;
