import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import {
    Navigation,
    CallApi,
    Homepage,
    Routines,
    Login,
    Register,
    Activities
} from './Components'

const {REACT_APP_BASE_URL = 'https://fitnesstrac-kr.herokuapp.com/api/'} = process.env;

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword ] = useState('');
    const [activities, setActivities] = useState([]);
    const [loggedIn, setLoggedIn] = useState(false);
    const [routines, setRoutines] = useState([]);
    const [token, setToken] = useState('');

    console.log( "ReactUrl", `${REACT_APP_BASE_URL}`)

    const fetchRoutines = async () => {
        try {
            const resp = await fetch (`${REACT_APP_BASE_URL}/routines`, {
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            const data = await resp.json();
            if (data){
                setRoutines(data)
            }

        } catch (error) {
            throw error
        }
    }

    const fetchActivities = async () => {
        try {
            const resp = await fetch (`${REACT_APP_BASE_URL}/activities`,{
                headers: {
                    'Content-Type': 'application/json'
            }});
            const data = await resp.json();
            if (data){
                setActivities(data);
            }

        } catch (error) {
            throw error;
        }
    }
   useEffect(() => {
       try {
           fetchRoutines();
           fetchActivities();

       } catch (error) {
           console.error(error);

       }
   }, [token]);

    useEffect(() => {
        const foundToken = localStorage.getItem('token');
        if(foundToken) {
          setToken(foundToken)
          console.log("<<<< found:", foundToken)
        }
      }, []);
    return <>
        <div>
            <Navigation token={token} setToken= {setToken} loggedIn = {loggedIn} setLoggedIn = {setLoggedIn} setUsername = {setUsername} setPassword = {setPassword}/>
            <div>
                <Switch>
                    <Route exact path = "/home">
                        <Homepage />
                    </Route>

                    {/* <Route exact path = "/account">

                    </Route> */}


                    <Route exact path = "/login">
                        <Login setToken= {setToken} token={token} setUsername = {setUsername}
                        setPassword = {setPassword} username = {username} password = {password} setLoggedIn = {setLoggedIn} />
                    </Route>

                    <Route exact path = "/register">
                        <Register setToken= {setToken} token={token} setUsername = {setUsername}
                        setPassword = {setPassword} username = {username} password = {password} setLoggedIn = {setLoggedIn} />
                    </Route>

                    <Route exact path = "/routines">
                        <Routines routines={routines} setRoutines={setRoutines} token={token} fetchRoutines={fetchRoutines} />
                    </Route>

                    <Route exact path = "/activities">
                        <Activities activities={activities} setActivities={setActivities} token={token} fetchActivities = {fetchActivities} />
                    </Route>

                </Switch>
            </div>
        </div>

    </>
}

ReactDOM.render(
    <Router>
        <App />
    </Router>,
  document.getElementById('app'),
);
